mapper.select:
select
id, name, tell, bname, price, preson, starttime, endtime, addr, rate
from water.order


        and id = #{id}


        and name = #{name}


        and tell = #{tell}


        and bname = #{bname}


        and price = #{price}


        and preson = #{preson}


        and starttime = #{starttime}


        and endtime = #{endtime}


        and addr = #{addr}


        and rate = #{rate}
